import { creatSelector } from 'reselect';

//input selector - get all state//

const selectCart = state => state.cart;

export const selectCartItems = creatSelector(
    [selectCart],
    (cart) => cart.cartItems
);

export const selectCartItemsCount = creatSelector(
    [selectCartItems],
    cartItems =>
        cartItems.reduce(
            (accumalatedQuantity, cartItem) => accumalatedQuantity + cartItem, 0
        )
)
